import React from 'react';
//Контекст
import { CurrentUserContext } from '../context/CurrentUserContext';

//Элементы
import editAvatar from '../images/editAvatar.svg';
import Card from './Card';
import api from '../utils/api.js';


function Main(props) {

    //Подписываемся на контект
    const currentUser = React.useContext(CurrentUserContext);

    const [cards, setCards] = React.useState([]);

    //Запрос карточек
    React.useEffect(() => {
        api.getInitialCards()
        .then((cards) => {     
            setCards(cards);
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
        });
    }, []);


    //Постановка и удаление лайка
    function handleCardLike (card) {
        // Снова проверяем, есть ли уже лайк на этой карточке
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        // Отправляем запрос в API и получаем обновлённые данные карточки
        api.changeLikeCardStatus(card._id, !isLiked)
        .then((likedCard) => {
            const newCards = cards.map((i) => i._id === card._id ? likedCard : i);
            setCards(newCards);
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
        });
    }

    //Удаление карточки
    function handleCardDelete (card) {
        api.deleteCard(card._id)
        .then((deleteCard) => {
            //console.log(deleteCard);
            //const newCards = cards.filter((i) => i._id !== deleteCard._id);
            setCards(cards => cards.filter(c => c.owner._id != deleteCard._id));
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
        });
    }


    return (
        <main className="content">
            <section className="profile">
                <div className="profile__person">
                    <div className="profile__avatar" style={{ backgroundImage: `url(${currentUser.avatar})` }} onClick={props.onEditAvatar}>
                        <div className="profile__avatar-edit">
                            <img src={editAvatar} className="profile__edit-icon" alt="Иконка редактирования аватара" />
                        </div>
                    </div>
                    <div className="profile__info">
                        <div className="profile__edit-info">
                            <h1 className="profile__name" name="name">{currentUser.name}</h1>
                            <button className="profile__edit-button" type="button" onClick={props.onEditProfile}></button>
                        </div>
                        <p className="profile__about" name="about">{currentUser.about}</p>
                    </div>
                </div>
                <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
            </section>


            <section className="cards">
                {cards.map((item) => {

                    return (
                        <Card
                        card={item}
                        key={item._id}
                        onCardClick={props.onCardClick}
                        onCardLike={handleCardLike}
                        onCardDelete={handleCardDelete} />
                    )
                })}
            </section>

        </main>
    )
}


export default Main;
