import React from 'react';
import PopupWithForm from './PopupWithForm';

function EditProfilePopup(props) {
    return (
        <PopupWithForm
        name="profile"
        title="Редактировать профиль"
        buttonTitle="Сохранить"
        isOpen={props.isOpen}
        onClose={props.onClose}>
            <input name="name" type="text" placeholder="Ваше имя" className="popup__input popup__input_name" id="popup__input_name" minLength="2" maxLength="40" required />
            <span className="popup__error popup__error_name" id="popup__input_name-error"></span>
            <input name="about" type="text" placeholder="О себе" className="popup__input popup__input_about" id="popup__input_about" minLength="2" maxLength="200" required />
            <span className="popup__error popup__error_about" id="popup__input_about-error"></span>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
