import React from 'react';
import PopupWithForm from './PopupWithForm';

function AddPlacePopup(props) {
    //Реф
    /*const cardNameRef = React.useRef();
    const cardLinkRef = React.useRef();

    function handleSubmit(e) {
        e.preventDefault();

        props.onAddPlace({
          name: cardNameRef.current.value,
          link: cardLinkRef.current.value,
        });

        e.target.reset();
    }*/

    //Стейт-переменные
    const [name, setName] = React.useState('');
    const [link, setLink] = React.useState('');

    //const [isNameInputValid, setIsNameInputValid] = React.useState(true);
    //const [isLinkInputValid, setIsLinkInputValid] = React.useState(true);
    //const [nameErrorMessage, setNameErrorMessage] = React.useState('');
    //const [linkErrorMessage, setLinkErrorMessage] = React.useState('');

    //const [buttonState, setbuttonState] = React.useState(false);


    //Обработчики
    function handleChangeName(e) {
        setName(e.target.value);
        //nameValidation(e.target);
    }

    function handleChangeLink(e) {
        setLink(e.target.value);
        //linkValidation(e.target);
    }

    function handleSubmit(e) {
        e.preventDefault();
        // Передаём значения управляемых компонентов во внешний обработчик
        props.onAddCard({
          name: name,
          link: link,
        });
    }

    React.useEffect(() => {
        setName('');
        setLink('');

    })


    return (
        <PopupWithForm
        name="place"
        title="Новое место"
        buttonTitle="Сохранить"
        isOpen={props.isOpen}
        onClose={props.onClose}
        onSubmit={handleSubmit}>
            <input
            name="name"
            type="text"
            placeholder="Название"
            className="popup__input popup__input_place"
            id="popup__input_place"
            minLength="2"
            maxLength="30"
            ref={cardNameRef}
            required />
            <span className="popup__error popup__error_place" id="popup__input_place-error"></span>

            <input
            name="link"
            type="url"
            placeholder="Ссылка"
            className="popup__input popup__input_link"
            id="popup__input_link"
            ref={cardLinkRef}
            required />
            <span className="popup__error popup__error_link" id="popup__input_link-error"></span>
        </PopupWithForm>
    )
}

export default AddPlacePopup;
