import React from 'react';
import editAvatar from '../images/editAvatar.svg';
import EditProfilePopup from './EditProfilePopup';
import EditPlacePopup from './EditPlacePopup';
import EditAvatarPopup from './EditAvatarPopup';


function Main() {
    //Открытие попапа аватара
    function handleEditAvatarClick() {
        const avatarPopup = document.querySelector('.popup_avatar');
        avatarPopup.classList.add('popup_opened');
    }

    //Открытие попапа профиля
    function handleEditProfileClick() {
        const profilePopup = document.querySelector('.popup_profile');
        profilePopup.classList.add('popup_opened');
    }

    //Открытие попапа карточек
    function handleAddPlaceClick() {
        const placePopup = document.querySelector('.popup_place');
        placePopup.classList.add('popup_opened');
    }

    return (
        <main className="content">
            <section className="profile">
                <div className="profile__person">
                    <div className="profile__avatar" onClick={handleEditAvatarClick}>
                        <div className="profile__avatar-edit">
                            <img src={editAvatar} className="profile__edit-icon" alt="Иконка редактирования аватара" />
                        </div>
                    </div>
                    <div className="profile__info">
                        <div className="profile__edit-info">
                            <h1 className="profile__name" name="name">Жак-Ив Кусто</h1>
                            <button className="profile__edit-button" type="button" onClick={handleEditProfileClick}></button>
                        </div>
                        <p className="profile__about" name="about">Исследователь океана</p>
                    </div>
                </div>
                <button className="profile__add-button" type="button" onClick={handleAddPlaceClick}></button>
            </section>


            <section className="cards"></section>

            <EditProfilePopup />

            <EditPlacePopup />

            <EditAvatarPopup />



            <section className="popup popup_card">
                <div className="popup__container popup__container_card">
                    <div className="popup__pic">
                        <button className="popup__close popup__close_card" type="button"></button>
                        <img className="popup__image" src="#" alt="#" />
                        <p className="popup__text"></p>
                    </div>
                </div>
            </section>

            <section className="popup popup_delete">
                <div className="popup__container">
                    <button className="popup__close popup__close-place" type="button"></button>
                    <h2 className="popup__title popup__title-delete">Вы уверены?</h2>
                    <form className="popup__form popup__form-delete" name="card-popup" id="delete-popup" noValidate>
                        <button className="popup__save-button popup__save-button_delete" id="delete-popup-button" type="submit">Да</button>
                    </form>
                </div>
            </section>

        </main>
    )
}

export default Main;
