import React from 'react';
import {api} from '../utils/Api';


function Main(props) {

    React.useEffect(() => {
        api.getUserInfo()
        .then((userInfo) => {
            props.userName = userInfo.name;
            props.userDescription = userInfo.about;
            /*const userName = document.querySelector('.profile__name');
            const userAbout = document.querySelector('.profile__about');

            userName.textContent = userInfo.name;
            userAbout.textContent = userInfo.about;*/
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
         });
    })


    return (
        <main className="content">
            <section className="profile">
                <div className="profile__person">
                    <div className="profile__avatar" onClick={props.onEditAvatar}>
                        <div className="profile__avatar-edit">
                            <img style={{ backgroundImage: `url(${props.userAvatar})` }}  className="profile__edit-icon" alt="Иконка редактирования аватара" />
                        </div>
                    </div>
                    <div className="profile__info">
                        <div className="profile__edit-info">
                            <h1 className="profile__name" name="name">{props.userName}</h1>
                            <button className="profile__edit-button" type="button" onClick={props.onEditProfile}></button>
                        </div>
                        <p className="profile__about" name="about">{props.userDescription}</p>
                    </div>
                </div>
                <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
            </section>


            <section className="cards">

            </section>

        </main>
    )
}

export default Main;
