    }*/

  return (
    <div className="page">
        <Header />

        <Main />

        <Footer />

        <EditProfilePopup />
        <EditPlacePopup />
        <EditAvatarPopup isOpen={handleEditAvatarClick}/>
        <DeletePopup />
        <ImagePopup />
    </div>
  );
}

export default App;
