import React from 'react';
import PopupWithForm from './PopupWithForm';

function EditPlacePopup(props) {
    //Стейт-переменные
    const [cardName, setCardName] = React.useState('');
    const [cardLink, setCardLink] = React.useState('');

    //Обработчики
    function handleAddCardName(e) {
        setCardName(e.target.value);
    }

    function handleAddCardLink(e) {
        setCardLink(e.target.value);
    }

    function handleSubmit(e) {
        e.preventDefault();
        // Передаём значения управляемых компонентов во внешний обработчик
        props.onAddPlace({
          place: cardName,
          url: cardLink,
        });
      }

    return (
        <PopupWithForm
        name="place"
        title="Новое место"
        buttonTitle="Сохранить"
        isOpen={props.isOpen}
        onClose={props.onClose}
        onSubmit={handleSubmit}>
            <input
            name="name"
            type="text"
            placeholder="Название"
            className="popup__input popup__input_place"
            id="popup__input_place"
            minLength="2"
            maxLength="30"
            onChange={handleAddCardName}
            required />
            <span className="popup__error popup__error_place" id="popup__input_place-error"></span>

            <input
            name="link"
            type="url"
            placeholder="Ссылка"
            className="popup__input popup__input_link"
            id="popup__input_link"
            onChange={handleAddCardLink}
            required />
            <span className="popup__error popup__error_link" id="popup__input_link-error"></span>
        </PopupWithForm>
    )
}

export default EditPlacePopup;
