import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import logo from '../images/logo.svg';

function Header(props) {
    const location = useLocation()
    console.log(location.pathname);

    return (
        <header className="header">
            <img src={logo} className="header__logo" alt="Логотип Mesto" />

            {props.loggedIn ? (
                <div className="header__user-info">
                    <p className="header__email">{props.userData.email}</p>
                    <Link to="/sign-in" className="header__sign-out">Выйти</Link>
                </div>
            ) : (
                <Link
                to={props.loginState ? "/sign-in" : "/sign-up"}
                className="header__link">

                {props.loginState ? "Войти" : "Регистрация"}
                </Link>
            )}
        </header>
    )
}

export default Header;
