import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import logo from '../images/logo.svg';

function Header(props) {
    const location = useLocation();
    const isLocationStart = location.pathname === '/';
    const isLocationSignIn = location.pathname === '/sign-in';

    function handleSignOut() {
        props.signOut();
    }

    function handleSignIn() {

    }

    return (
        <header className="header">
            <img src={logo} className="header__logo" alt="Логотип Mesto" />

            {(isLocationStart) && 
                <div className="header__user-info">
                    <p className="header__email">{props.loggedIn ? props.userData.email : ""}</p>

                    {isLocationSignIn ? (
                        <Link
                        to="/sign-up"
                        className="header__link"
                        >
                        {!props.loggedIn ? "Регистрация" : ""}
                        </Link>
                        ) : (
                        <Link to="/sign-in"
                        className={`header__link ${isLocationStart ? "header__sign-out" : ""}`}
                        onClick={props.loggedIn ? handleSignOut : handleSignIn}>
                        {props.loggedIn ? "Выйти" : "Войти"}
                        </Link>

                        )
                    }


            </div>
            }
        </header>
    )
}

export default Header;
