import React from 'react';
import {api} from '../utils/api';
import Card from './Card';


function Main(props) {
    const [currentUser, setCurrentUser] = React.useState({});
    const [cards, setCards] = React.useState([]);

    //Запрос информации пользователя
    React.useEffect(() => {
        api.getUserInfo()
        .then((userInfo) => {
            setCurrentUser(userInfo);
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
         });
    })


    //Запрос карточек
    React.useEffect(() => {
        api.getInitialCards()
        .then((cards) => {     
            setCards(cards);
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
        });
    }, []);


    return (
        <main className="content">
            <section className="profile">
                <div className="profile__person">
                    <div className="profile__avatar" style={{ backgroundImage: `url(${props.userAvatar})` }} onClick={props.onEditAvatar}>
                        <div className="profile__avatar-edit">
                            <img src={currentUser.avatar} className="profile__edit-icon" alt="Иконка редактирования аватара" />
                        </div>
                    </div>
                    <div className="profile__info">
                        <div className="profile__edit-info">
                            <h1 className="profile__name" name="name">{currentUser.name}</h1>
                            <button className="profile__edit-button" type="button" onClick={props.onEditProfile}></button>
                        </div>
                        <p className="profile__about" name="about">{currentUser.about}</p>
                    </div>
                </div>
                <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
            </section>


            <section className="cards">
                {props.cards.map((item) => {
                    //console.log(item);
                    return <Card card={item} key={item._id} onCardClick={props.onCardClick} />
                })}
            </section>

        </main>
    )
}


export default Main;
