import React from 'react';

//Контекст
import { CurrentUserContext } from '../context/CurrentUserContext';

//Основное
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import api from '../utils/api.js';

//Попапы
import EditProfilePopup from './EditProfilePopup';
import EditPlacePopup from './EditPlacePopup';
import EditAvatarPopup from './EditAvatarPopup';
import DeletePopup from './DeletePopup';
import ImagePopup from './ImagePopup';

import '../index.css';

function App() {
    //Переменные состояния
    const [currentUser, setCurrentUser] = React.useState({});

    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [card, setCard] = React.useState({name: '', link: ''});
    const [isImagePopupOpen, setIsImagePopupOpen] = React.useState(false);


    //Запрос информации пользователя
    React.useEffect(() => {
        api.getUserInfo()
        .then((userInfo) => {
            setCurrentUser(userInfo);
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
         });
    }, []);



    //Открытие попапа аватара
    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
    }

    //Открытие попапа профиля
    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
    }

    //Открытие попапа карточек
    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
    }

    //Открытие превью карточки
    function handleCardClick(card) {
        setIsImagePopupOpen(!isImagePopupOpen);
        setCard({
            name: card.name,
            link: card.link
        });
    }

    //Обработчик данных профиля
    function handleUpdateUser(items) {
        api.setUserInfo(items)
            .then((userInfo) => {
                setCurrentUser(userInfo);
                closeAllPopups();
            })
            .catch((err) => {
                console.log(`Attention! ${err}`)
            });
    }

    //Обработчик данных аватара
    function handleUpdateAvatar(item) {
        api.setNewAvatar(item)
            .then((data) => {
                console.log(data);
                setCurrentUser(data.avatar);
                closeAllPopups();
            })
            .catch((err) => {
                console.log(`Attention! ${err}`)
            })

    }
    //https://sun1-87.userapi.com/s/v1/if1/BsuHlFEqXhHSDg9kTkqKEpTjsUoRF55hAqzZ0-thXHIbqunVMWDJO1OE8beOWvon2QZIz3Mj.jpg?size=400x0&quality=96&crop=0,0,827,828&ava=1

    //Закрытие попапов
    function closeAllPopups() {
        setIsEditAvatarPopupOpen(false);
        setIsEditProfilePopupOpen(false);
        setIsAddPlacePopupOpen(false);
        setIsImagePopupOpen(false);
    }


  return (
      <CurrentUserContext.Provider value={currentUser}>
          <div className="page">
            <Header />

            <Main 
            onEditAvatar={handleEditAvatarClick}
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onCardClick={handleCardClick}
            />

            <Footer />

            <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
            />

            <EditPlacePopup
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            />

            <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
            />

            <DeletePopup />
            <ImagePopup
            card={card}
            isOpen={isImagePopupOpen}
            onClose={closeAllPopups}
            />
        </div>
      </CurrentUserContext.Provider>

  )
}

export default App;
