import React from 'react';

//Контекст
import { CurrentUserContext } from '../context/CurrentUserContext';

//Основное
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import api from '../utils/api.js';

//Попапы
import EditProfilePopup from './EditProfilePopup';
import EditPlacePopup from './EditPlacePopup';
import EditAvatarPopup from './EditAvatarPopup';
import DeletePopup from './DeletePopup';
import ImagePopup from './ImagePopup';

import '../index.css';

function App() {
    //Переменные состояния
    const [currentUser, setCurrentUser] = React.useState({});

    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [card, setCard] = React.useState({name: '', link: ''});
    const [isImagePopupOpen, setIsImagePopupOpen] = React.useState(false);

    //Карточки
    const [cards, setCards] = React.useState([]);

    //Переменная активности кнопки сабмита
    const [submitButton, setSubmitButton] = React.useState(false);
    const [isNameValid, setIsNameValid] = React.useState(false);
    const [isDescriptionValid, setIsDescriptionValid] = React.useState(false);

    React.useEffect(() => {
        if (isNameValid === true && isDescriptionValid === true) {
            setSubmitButton(true);
        } else {
            setSubmitButton(false);
        }
      }, [isNameValid, isDescriptionValid]);


    //Запрос информации пользователя
    React.useEffect(() => {
        api.getUserInfo()
        .then((userInfo) => {
            setCurrentUser(userInfo);
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
         });
    }, []);



    //Открытие попапа аватара
    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
    }

    //Открытие попапа профиля
    function handleEditProfileClick() {
        setIsNameValid(false);
        setIsDescriptionValid(false);
        setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
    }

    //Открытие попапа карточек
    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
    }

    //Открытие превью карточки
    function handleCardClick(card) {
        setIsImagePopupOpen(!isImagePopupOpen);
        setCard({
            name: card.name,
            link: card.link
        });
    }

    //Обработчик данных профиля
    function handleUpdateUser(items) {     
        api.setUserInfo(items)
            .then((userInfo) => {
                setCurrentUser(userInfo);
                closeAllPopups();
            })
            .catch((err) => {
                console.log(`Attention! ${err}`)
            });
    }

    //Обработчик данных аватара
    function handleUpdateAvatar(item) {
        api.setNewAvatar(item)
            .then((data) => {
                setCurrentUser(data);
                closeAllPopups();
            })
            .catch((err) => {
                console.log(`Attention! ${err}`)
            })

    }

    //Закрытие попапов
    function closeAllPopups() {
        setIsEditAvatarPopupOpen(false);
        setIsEditProfilePopupOpen(false);
        setIsAddPlacePopupOpen(false);
        setIsImagePopupOpen(false);
    }


     //Запрос карточек
     React.useEffect(() => {
        api.getInitialCards()
        .then((cards) => {     
            setCards(cards);
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
        });
    }, []);


    //Постановка и удаление лайка
    function handleCardLike(card) {
        // Снова проверяем, есть ли уже лайк на этой карточке
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        // Отправляем запрос в API и получаем обновлённые данные карточки
        api.changeLikeCardStatus(card._id, !isLiked)
        .then((likedCard) => {
            const newCards = cards.map((i) => i._id === card._id ? likedCard : i);
            setCards(newCards);
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
        });
    }

    //Удаление карточки
    function handleCardDelete(card) {
        api.deleteCard(card._id)
        .then((deleteCard) => {
            //console.log(deleteCard);
            const newCards = cards.filter((i) => i._id !== deleteCard._id);
            setCards(newCards);
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
        });
    }

    //Обработка данных новой карточки
    function handleAddPlaceSubmit(items) {
        console.log(cards);
        api.addCard(items)
        .then((newCard) => {
            setCards([newCard, ...cards]);
            closeAllPopups();
        })
        .catch((err) => {
            console.log(`Attention! ${err}`);
        });
    }



  return (
      <CurrentUserContext.Provider value={currentUser}>
          <div className="page">
            <Header />

            <Main 
            onEditAvatar={handleEditAvatarClick}
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onCardClick={handleCardClick}
            cards={cards}
            onCardLike={handleCardLike}
            onCardDelete={handleCardDelete}
            />

            <Footer />

            <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
            submitButton={submitButton}
            isNameValid={isNameValid}
            isDescriptionValid={isDescriptionValid}
            />

            <EditPlacePopup
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            onAddPlace={handleAddPlaceSubmit}
            />

            <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
            />

            <DeletePopup />
            <ImagePopup
            card={card}
            isOpen={isImagePopupOpen}
            onClose={closeAllPopups}
            />
        </div>
      </CurrentUserContext.Provider>

  )
}

export default App;
