import React from 'react';
import PopupWithForm from './PopupWithForm';

function AddPlacePopup(props) {
    //Реф
    /*const cardNameRef = React.useRef();
    const cardLinkRef = React.useRef();

    function handleSubmit(e) {
        e.preventDefault();

        props.onAddPlace({
          name: cardNameRef.current.value,
          link: cardLinkRef.current.value,
        });

        e.target.reset();
    }*/

    //Стейт-переменные
    const [name, setName] = React.useState('');
    const [link, setLink] = React.useState('');

    const [isNameInputValid, setIsNameInputValid] = React.useState(true);
    const [isLinkInputValid, setIsLinkInputValid] = React.useState(true);
    const [nameErrorMessage, setNameErrorMessage] = React.useState('');
    const [linkErrorMessage, setLinkErrorMessage] = React.useState('');

    const [buttonState, setButtonState] = React.useState(false);
    const [isNameInputInitial, setNameInputInitial] = React.useState(true);
    const [isLinkInputInitial, setLinkInputInitial] = React.useState(true);


    //Обработчики
    function handleChangeName(e) {
        setName(e.target.value);
        nameValidation(e.target);
    }

    function handleChangeLink(e) {
        setLink(e.target.value);
        linkValidation(e.target);
    }

    function handleSubmit(e) {
        e.preventDefault();
        props.onAddCard({name, link});
    }

    React.useEffect(() => {
        setName('');
        setLink('');
        setNameErrorMessage('');
        setLinkErrorMessage('');
        setIsNameInputValid(true);
        setIsLinkInputValid(true);
        setButtonState(false);
    }, [props.isOpen]);

    //Валидация формы
    React.useEffect(()=> {
        if (isNameInputValid && isLinkInputValid && !isNameInputInitial && !isLinkInputInitial) {
          setButtonState(true);
        } else {
          setButtonState(false);
        }
        setIsNameInputValid(false);
        setIsLinkInputValid(false);
      }, [isNameInputValid, isLinkInputValid, isNameInputInitial, isLinkInputInitial]);

    function nameValidation(input) {
        if (!input.validity.valid) {
          setIsNameInputValid(false);
          setNameInputInitial(false);
          setNameErrorMessage(input.validationMessage);
        } else {
          setIsNameInputValid(true);
          setNameInputInitial(true);
        }
    }

    function linkValidation(input) {
        if (!input.validity.valid) {
          setIsLinkInputValid(false);
          setLinkInputInitial(false);
          setLinkErrorMessage(input.validationMessage);
        } else {
          setIsLinkInputValid(true);
          setLinkInputInitial(true);
        }
    }


    return (
        <PopupWithForm
        name="place"
        title="Новое место"
        buttonTitle="Сохранить"
        isOpen={props.isOpen}
        onClose={props.onClose}
        onSubmit={handleSubmit}
        buttonState={buttonState}>
            <input
            name="name"
            type="text"
            placeholder="Название"
            className={`popup__input popup__input_place ${!isNameInputValid ? '' : 'popup__input_invalid'}`}
            id="popup__input_place"
            minLength="2"
            maxLength="30"
            onChange={handleChangeName}
            required />
            <span
            className={`popup__error popup__error_name ${!isNameInputValid ? 'popup__error_active' : ''}`}
            id="popup__input_place-error"
            >{nameErrorMessage}</span>

            <input
            name="link"
            type="url"
            placeholder="Ссылка"
            className={`popup__input popup__input_link ${!isLinkInputValid ? '' : 'popup__input_invalid'}`}
            id="popup__input_link"
            onChange={handleChangeLink}
            required />
            <span
            className={`popup__error popup__error_link ${!isLinkInputValid ? 'popup__error_active' : ''}`}
            id="popup__input_link-error"
            >{linkErrorMessage}</span>
        </PopupWithForm>
    )
}

export default AddPlacePopup;
