import React from 'react';
import PopupWithForm from './PopupWithForm';

function EditPlacePopup(props) {
    //Реф
    const cardNameRef = React.useRef();
    const cardLinkRef = React.useRef();

    function handleSubmit(e) {
        e.preventDefault();

        props.onAddPlace({
          name: cardNameRef.current.value,
          link: cardLinkRef.current.value,
        });

        e.target.reset();
    } 


    return (
        <PopupWithForm
        name="place"
        title="Новое место"
        buttonTitle="Сохранить"
        isOpen={props.isOpen}
        onClose={props.onClose}
        onSubmit={handleSubmit}>
            <input
            name="name"
            type="text"
            placeholder="Название"
            className="popup__input popup__input_place"
            id="popup__input_place"
            minLength="2"
            maxLength="30"
            ref={cardNameRef}
            required />
            <span className="popup__error popup__error_place" id="popup__input_place-error"></span>

            <input
            name="link"
            type="url"
            placeholder="Ссылка"
            className="popup__input popup__input_link"
            id="popup__input_link"
            ref={cardLinkRef}
            required />
            <span className="popup__error popup__error_link" id="popup__input_link-error"></span>
        </PopupWithForm>
    )
}

export default EditPlacePopup;
