import React from 'react';

function PopupWithForm(props) {
    return (
    <section className={`popup popup_${props.name}`}>
        <div className="popup__container">
            <button className={`popup__close popup__close-${props.name}`} type="button"></button>
            <h2 className="popup__title">{props.title}</h2>
            <form className={`popup__form popup__form_${props.name}`} name="profile-popup" id="profile-popup" noValidate>
                <input name="name" type="text" placeholder="Ваше имя" className="popup__input popup__input_name" id="popup__input_name" minLength="2" maxLength="40" required />
                <span className="popup__error popup__error_name" id="popup__input_name-error"></span>
                <input name="about" type="text" placeholder="О себе" className="popup__input popup__input_about" id="popup__input_about" minLength="2" maxLength="200" required />
                <span className="popup__error popup__error_about" id="popup__input_about-error"></span>
                <button className="popup__save-button popup__save-button-profile" id="profile-popup-button" type="submit">Сохранить</button>
            </form>
        </div>
    </section>
    )
}

export default PopupWithForm;
